import { useState } from "react"
function App(){
  let [count, setCount] = useState(0)

  const handleClick = () => {
    setCount(count ++)
  }

  return(
    <div className="box">
      <button className="button is-primary" onClick={handleClick}>Add Animal</button>
      <p>Number of animals is : {count} </p>
    </div>
  )
}

export default App
