import { useState } from "react"
function App(){
  const [count, setCount] = useState(0)
  const handleClick = () => {
    console.log(useState(0));
    setCount(count + 1)
  }

  return(
    <div className="box">
      <button className="button is-primary" onClick={handleClick}>Add Animal</button>
      <p>Number of animals is : {count} </p>
    </div>
  )
}

export default App
